/* showcase */
.showcase {
    background: linear-gradient(to top, color(primary), color(secondary));
    width: 100%;
    padding: 2rem;
    @include title-section-styled(primary);

    &-wrapper {
        display: flex;
        @media (max-width: 1000px) {
            & {
                flex-wrap: wrap;
                justify-content: center;
            }
        }
        @media (max-width: 600px) {
            & {
                flex-direction: column;
                align-items: center;
            }
        }
    }

    &-card {
        @media (max-width: 600px) {
            & {
                width: 80%;
            }
        }

        &-image {
            width: 20rem;
            height: 30rem;
            object-fit: cover;
            padding: 1rem;
            box-shadow: 0 3rem 5rem rgba(0, 0, 0, .6);
            border-radius: .5rem;
            transition: all .2s;

            &:hover {
                transform: skewY(2deg);
            }
        }

        &-content {
            background-color: color(quaternary);
            box-shadow: 0 3rem 5rem rgba(0, 0, 0, .6);
            border-radius: .9rem;
            margin-top: 1.5rem;
            opacity: .8;
            padding: 1rem;
            position: relative;
            transition: all 1.1s;

            &:hover {
                transform: translateY(-8rem) rotate(15deg);
                opacity: 1;
            }

            &-title {
                font-size: size(lg);
                font-weight: 300;
                font-style: italic;
                color: color(secondary);
                padding-top: 1.2rem;

                @media (max-width: 1200px) {
                    & {
                        font-size: size(md);
                    }
                }
            }

            &-author {
                font-size: size(xs) + .3rem;
                font-weight: 100;
                padding: 1rem;
            }

            &-price {
                font-size: size(xl);
                text-align: end;
                font-weight: 200;
                color: color(primary);

                @media (max-width: 1200px) {
                    & {
                        font-size: size(md);
                    }
                }
            }

            i {
                position: absolute;
                left: 2rem;
                bottom: 2rem;
                font-size: size(lg);
                color: color(primary);
                transition: all .5s;

                @media (max-width: 1200px) {
                    & {
                        font-size: size(sm);
                    }
                }

                &:hover {
                    color: color(secondary);
                    cursor: pointer;
                }
            }
        }
    }
}
/* end of showcase */

