    @media (max-width: 800px) {
        & {
            height: auto;
            padding: 1rem;
        }
    }

    &-wrapper {
        display: flex;
        height: width 100%;
        justify-content: space-evenly;
        align-items: center;  
        z-index: 100;
        @media (max-width: 800px) {
            & {
                flex-wrap: wrap;
                flex-direction: column-reverse;
            }
        }
    }

    &-navigation {
        z-index: 10;
        ul {
            list-style: none;
            padding-top: 3rem;
            @media (max-width: 800px) {
                & {
                    padding-top: 1rem;
                    padding-bottom: 2rem;
                    display: flex;
                    flex-wrap: wrap;
                    justify-content: center;
                }
            }

            li {
                padding: .7rem;
                a {
                    color: color(primary);
                    text-decoration: none;
                    font-size: size(md);
                    transition: all .5s;
                    @media (max-width: 1000px) {
                        & {
                            font-size: size(sm);
                        }
                    }
                    @media (max-width: 800px) {
                        & {
                            font-size: size(xs) + .3rem;
                        }
                    }

                    &:hover {
                        color: color(tertiary);
                        &:after {
                            content: " ➩"
                        }
                    }
                }
            }
        }
    }

    &-contact {
        z-index: 10;
        @media (max-width: 800px) {
            & {
                padding-bottom: 2rem;
            }
        }
        @media (max-width: 450px) {
            form {
                display: flex;
                flex-direction: column;
                text-align: center;
                padding-top: 2rem;
            }
        }

        label {
            color: color(primary);
            font-size: size(md);
            @media (max-width: 1000px) {
                & {
                    font-size: size(sm);
                }
            }
            @media (max-width: 800px) {
                & {
                    font-size: size(xs) + .3rem;
                }
            }

        }
        input {
            outline: none;
            background: transparent;
            border: 1px solid color(primary);
            height: 3rem;
            border-radius: 2rem;
            padding: 1rem;
            margin-left: .5rem;
            font-size: size(sm);

            @media (max-width: 1000px) {
                & {
                    font-size: size(sm);
                }
            }
            @media (max-width: 800px) {
                & {
                    font-size: size(xs) + .3rem;
                }
            }
        }

        button {
            background: transparent;
            outline: none;
            border: none;
            border: 1px solid color(primary);
            border-radius: 50%;
            padding: .8rem;
            transition: all .4s;


            i {
                font-size: size(xs);
            }

            &:hover {
               background-color: color(primary);
               color: color(light);
               cursor: pointer;
            }
            @media (max-width: 450px) {
                & {
                    max-width: min-content;
                    margin-left: 50%;
                    margin-top: .5rem;
                    transform: translateX(-50%);
                }
            }
        }
    }

    &-arrow {
        z-index: 10;
        font-size: size(xl) + 3rem;

        @media (max-width: 1000px) {
            & {
                font-size: size(xl) + 1.5rem;
            }
        }
        @media (max-width: 800px) {
            & {
                font-size: size(xl) + 1rem;
            }
        }

        i {
            outline: none;
            text-decoration: none;
            color: color(primary);
            @include icon-animated;
        }
    }

    &-svg {
        height: 20%;
        width: 100%;
        clip-path: polygon(20% 0%, 0% 20%, 30% 50%, 0% 80%, 20% 100%, 50% 70%, 80% 100%, 100% 80%, 70% 50%, 100% 20%, 80% 0%, 50% 30%);
        position: absolute;
    }

    &-copyright {
        text-align: center;
        font-size: size(xs);
        @media (max-width: 800px) {
            & {
                font-size: 1rem
            }
        }
    }
}
/* end of footer */
